import { Component } from '@angular/core';
import { PostService } from '../services/post.service';

@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent {

  csvData: any;

  constructor(private postService: PostService) { }

  ngOnInit(): void {
    this.getCsvData();
  }

  getCsvData() {
    this.postService.getCsvData('assets/posts_data.csv').subscribe(
      data => {
        this.csvData = this.parseCsvData(data);
        console.log(this.csvData)
      },
      error => {
        console.log('Si è verificato un errore:', error);
      }
    );
  }

  parseCsvData(csvData: string): any[] {
    // Implementa il parsing del CSV qui
    // Ad esempio, puoi dividere le righe e le colonne
    return csvData.split('\n').map(row => row.split(';'));
  }
}
<h1>Tutti i Post</h1>
<div *ngFor="let post of csvData">
    <div class="post_box">
        <h1>{{post}}</h1>
    </div>
</div>
