import { Component, OnInit } from '@angular/core';
import { PostService } from '../services/post.service';
import { Post } from '../models/post.model';
import { NavigationEnd, Router } from '@angular/router';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent implements OnInit {

  post_list: Post[] = [];
  private routerSubscription: Subscription;


  constructor(private postService: PostService, private router: Router) {
    this.routerSubscription = this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd && event.url === '/blog') {
        this.getCsvData();
      }
    });
   }

  // ngOnInit(): void {
  //   console.log('BlogComponent inizializzato');
  //   this.router.events.subscribe((event) => {
  //     if (event instanceof NavigationEnd && event.url === '/blog') {
  //       // Chiamata alla funzione per ricaricare i dati del blog
  //       this.getCsvData();
  //     }
  //   });

  // }

  ngOnDestroy(): void {
    if (this.routerSubscription) {
      this.routerSubscription.unsubscribe();
    }
  }

  getCsvData() {
    this.postService.getCsvData('assets/posts_data.csv').subscribe(
      data => {
        this.post_list = this.parseCsvData(data);
        console.log(this.post_list)
      },
      error => {
        console.log('Si è verificato un errore:', error);
      }
    );
  }

  parseCsvData(csvData: string): Post[] {
    // Implementa il parsing del CSV qui
    // Ad esempio, puoi dividere le righe e le colonne
    let post_string: string[][] = csvData.split('\n').map(row => row.split(';'));
    let posts: Post[] = [];
    for (const iterator of post_string) {
      let post: Post = {
        id: Number(iterator[0]),
        title: iterator[1],
        category: iterator[2],
        content: iterator[3],
        image: iterator[4],
        date: iterator[5]
      };
      posts.push(post);
    }
    return posts;
  }

  redirectToPostDatails(post: Post): void{
    this.router.navigate(['/post', post.id])
  }

}
<h1>Tutti i Post</h1>
<div *ngFor="let post of post_list">
    <div class="post_box" (click)="redirectToPostDatails(post)">
        <p>{{post.id}}</p>
        <p>{{post.title}}</p>
        <p>{{post.category}}</p>
        <p>{{post.content}}</p>
        <p>{{post.date}}</p>
    </div>
</div>
