// post.service.ts

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PostService {
  private baseUrl = 'http://localhost:3000'; // Sostituisci con l'URL del tuo server Node.js

  constructor(private http: HttpClient) {}

  getPostList(): Observable<string[][]> {
    return this.http.get<string[][]>(`${this.baseUrl}/api/posts`);
  }

  getPostContent(filename: string): Observable<string[]> {
    return this.http.get<string[]>(`${this.baseUrl}/api/posts/${filename}`);
  }
}
